//  мешаем цвета
let colors = ['red', 'red', 'red', 'red', 'red', 'red', 'red', 'red', 'red', 'blue', 'blue', 'blue', 'blue', 'blue', 'blue', 'blue', 'blue', 'black', 'white', 'white', 'white', 'white', 'white', 'white', 'white']
function colorsMix(){
	for (let i = 0; i < cardsCount-1; i++) {
		let randColor = Math.round(Math.random() * colors.length-1 )
		if(i !==randColor && randColor>=0 && randColor<cardsCount){
			const tmp = colors[i]
			colors[i] = colors[randColor]
			colors[randColor]= tmp
		}
	
	}
	console.log(colors);
}
class NewWord{
	constructor(color,word){
		this.color = color
		this.word = word
		this.activ = true
	}
}


function getLS(){
    const data = localStorage.getItem('gameWords');
    return data ? JSON.parse(data) : [];
}

function setLS(key,arr){
	localStorage.setItem(key, JSON.stringify(arr))
}

function setLSActiv(key,i){
	let dataLS = getLS()
	dataLS[i].activ = false
	setLS(key, dataLS)
}

// рендер карточек
function renderCards(){
	count()
	let numRandWord;
	rootDiv.innerText = '';
	const cardsDiv = document.createElement('div')
	cardsDiv.classList.add('cards')
	rootDiv.append(cardsDiv)

	if(getLS().length){

		const localStorageWords = getLS()
		// console.log(localStorageWords);
		localStorageWords.forEach((el,i) => {
			// console.log(`${i} = '${el.color}'`);
			const cardDiv = document.createElement('div')
			cardDiv.classList.add('card')
			cardsDiv.append(cardDiv)
			cardDiv.innerText = el.word
			if(el.activ){
				cardDiv.classList.add('activ')
			} else {
				cardDiv.style.backgroundColor= el.color
				// console.log(el.activ);
			}
			
			cardDiv.addEventListener('click', () =>  {
				if(el.activ){
					cardDiv.classList.remove('activ')
					el.activ = false
					setLSActiv('gameWords',i)
					switch (el.color) {
						case 'black':
								cardDiv.style.backgroundColor ='black'
								cardDiv.style.color ='white'
								setTimeout(() => {
									alert(' Всё кончено!!!')
									
								}, 100);
							break;
						case 'red':
							cardDiv.style.backgroundColor ='red'
							countDivPlRed.innerText=count().red
							
							break;
						case 'blue':
							cardDiv.style.backgroundColor ='blue'
							countDivPlBlue.innerText=count().blue
							break;
						case 'white':
							cardDiv.style.backgroundColor ='white'
							break;
					
						default:
							console.log('OOps');
							break;
					}
					
				}
	
			})
		});



	}else{
		colorsMix()
		for (let i = 0; i < cardsCount; i++) {
			const cardDiv = document.createElement('div')
			cardDiv.classList.add('card')
			cardDiv.classList.add('activ')
			numRandWord = Math.round(Math.random()*dataWords.length)

			const newWord = new NewWord(colors[i], dataWords[numRandWord])
			gameWords.push(newWord)
			gameWords[i].color = colors[i]
			gameWords[i].word = dataWords[numRandWord]

			setLS('gameWords', gameWords)
			cardDiv.innerText = dataWords[numRandWord]
			cardsDiv.append(cardDiv)
			
			
			cardDiv.addEventListener('click',() =>  {
				if(gameWords[i].activ){
					

					cardDiv.classList.remove('activ')
					gameWords[i].activ = false

				setLSActiv('gameWords',i)
					switch (gameWords[i].color) {
						case 'black':
								cardDiv.style.backgroundColor ='black'
								cardDiv.style.color ='white'
								setTimeout(() => {
									alert('Вы снова проиграли корову!!!')
									
								}, 100);
							break;
						case 'red':
							cardDiv.style.backgroundColor ='red'
							console.log('el red');
							count()

							break;
						case 'blue':
							cardDiv.style.backgroundColor ='blue'
							console.log('els blue');
							console.log(count().blue);
							count()

							break;
						case 'white':
							cardDiv.style.backgroundColor ='white'
							break;
					
						default:
							console.log('OOps');
							break;
					}
		
				}

			})
			
			
		}
		console.log(gameWords);

	}
			
			console.log('----------------------------');

		}


renderCards()



